import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const SuperMario = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [lives, setLives] = useState(5);
  const [score, setScore] = useState(0);
  const canvasRef = useRef(null);

  useEffect(() => {
    if (gameStarted) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      
      // Carica le immagini
      const marioImage = new Image();
      marioImage.src = 'data:image/jpeg;base64,'
      const backgroundImage = new Image();
      backgroundImage.src = 'data:image/jpeg;base64,';
      const mushroomImage = new Image();
      mushroomImage.src = 'data:image/jpeg;base64,';

      // Impostazioni di base del gioco
      let marioX = 50;
      let marioY = canvas.height - 100;
      const marioWidth = 50;
      const marioHeight = 80;
      let isJumping = false;
      let jumpHeight = 0;
      
      const mushrooms = [];
      for (let i = 0; i < 10; i++) {
        mushrooms.push({
          x: Math.random() * (canvas.width - 30) + canvas.width,
          y: canvas.height - 30,
          width: 30,
          height: 30
        });
      }

      // Funzione per disegnare Mario
      const drawMario = () => {
        ctx.drawImage(marioImage, marioX, marioY - jumpHeight, marioWidth, marioHeight);
      };

      // Funzione per disegnare lo sfondo
      const drawBackground = () => {
        ctx.drawImage(backgroundImage, 0, 0, canvas.width, canvas.height);
      };

      // Funzione per disegnare i funghi
      const drawMushrooms = () => {
        mushrooms.forEach(mushroom => {
          ctx.drawImage(mushroomImage, mushroom.x, mushroom.y, mushroom.width, mushroom.height);
        });
      };

      // Funzione per pulire il canvas
      const clearCanvas = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
      };

      // Funzione per gestire il salto
      const jump = () => {
        if (!isJumping) {
          isJumping = true;
          const jumpInterval = setInterval(() => {
            if (jumpHeight < 150) {
              jumpHeight += 5;
            } else {
              clearInterval(jumpInterval);
              fall();
            }
          }, 20);
        }
      };

      // Funzione per gestire la caduta
      const fall = () => {
        const fallInterval = setInterval(() => {
          if (jumpHeight > 0) {
            jumpHeight -= 5;
          } else {
            clearInterval(fallInterval);
            isJumping = false;
          }
        }, 20);
      };

      // Funzione per controllare le collisioni
      const checkCollisions = () => {
        mushrooms.forEach(mushroom => {
          if (marioX < mushroom.x + mushroom.width &&
              marioX + marioWidth > mushroom.x &&
              marioY < mushroom.y + mushroom.height &&
              marioY + marioHeight > mushroom.y) {
            if (jumpHeight > 0) {
              // Mario ha schiacciato il fungo
              setScore(prevScore => prevScore + 10);
              mushroom.x = canvas.width + Math.random() * 300;
            } else {
              // Mario è stato colpito dal fungo
              setLives(prevLives => prevLives - 1);
              setScore(prevScore => prevScore - 10);
              mushroom.x = canvas.width + Math.random() * 300;
            }
          }
        });
      };

      // Loop principale del gioco
      const gameLoop = () => {
        clearCanvas();
        drawBackground();
        drawMushrooms();
        drawMario();
        checkCollisions();

        // Muovi i funghi
        mushrooms.forEach(mushroom => {
          mushroom.x -= 2;
          if (mushroom.x < -30) {
            mushroom.x = canvas.width + Math.random() * 300;
          }
        });

        if (lives > 0) {
          requestAnimationFrame(gameLoop);
        } else {
          alert("Game Over! Punteggio finale: " + score);
          setGameStarted(false);
        }
      };

      // Avvia il loop del gioco
      gameLoop();

      // Gestione degli eventi da tastiera
      const handleKeyDown = (e) => {
        if (e.key === 'ArrowRight') {
          marioX += 5;
        } else if (e.key === ' ') {
          jump();
        }
      };

      window.addEventListener('keydown', handleKeyDown);

      // Pulizia degli event listener
      return () => {
        window.removeEventListener('keydown', handleKeyDown);
      };
    }
  }, [gameStarted, lives, score]);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>404 - Pagina non trovata</h1>
      {!gameStarted ? (
        <div>
          <p>Oops! Sembra che ti sia perso. Vuoi giocare a Super Mario o tornare alla homepage?</p>
          <button onClick={() => setGameStarted(true)}>Gioca a Super Mario</button>
          <Link to="/">
            <button>Torna alla Homepage</button>
          </Link>
        </div>
      ) : (
        <div>
          <canvas ref={canvasRef} width={800} height={400} style={{ border: '1px solid black' }} />
          <p>Vite: {lives} | Punteggio: {score}</p>
          <p>Usa la freccia destra per muoverti e la barra spaziatrice per saltare</p>
          <button onClick={() => setGameStarted(false)}>Smetti di giocare</button>
        </div>
      )}
    </div>
  );
};

export default SuperMario;